            'rgba(249, 168, 212, 0.6)',
            'rgba(251, 207, 232, 0.8)'
        ]
    };

    var bubbleIntervalId = null;

    function createBubble(particlesBg) {
        var bubble = document.createElement('div');
        bubble.className = 'konata-bubble';

        var size = Math.random() * (CONFIG.bubbleSizeMax - CONFIG.bubbleSizeMin) + CONFIG.bubbleSizeMin;
        var duration = Math.random() * (CONFIG.bubbleDurationMax - CONFIG.bubbleDurationMin) + CONFIG.bubbleDurationMin;
        var color = CONFIG.bubbleColors[Math.floor(Math.random() * CONFIG.bubbleColors.length)];
        var swayX = (Math.random() - 0.5) * 120;
        var swayXEnd = (Math.random() - 0.5) * 120;

        bubble.style.width = size + 'px';
        bubble.style.height = size + 'px';
        bubble.style.left = (Math.random() * 100) + '%';
        bubble.style.background = 'radial-gradient(circle at 35% 35%, ' + color + ', transparent 65%)';
        bubble.style.animationDuration = duration + 's';
        bubble.style.animationDelay = (Math.random() * 2) + 's';
        bubble.style.setProperty('--sway-x', swayX + 'px');
        bubble.style.setProperty('--sway-x-end', swayXEnd + 'px');

        particlesBg.appendChild(bubble);

        setTimeout(function() {
            if (bubble.parentNode) {
                bubble.parentNode.removeChild(bubble);
            }
        }, (duration + 3) * 1000);
    }

    function init() {
        var details = document.querySelector('.konata-thinking-details');
        var particlesBg = document.querySelector('.konata-particles-bg');

        if (!details || !particlesBg) {
            setTimeout(init, 200);
            return;
        }

        if (details.hasAttribute('data-konata-init')) return;
        details.setAttribute('data-konata-init', 'true');

        details.addEventListener('toggle', function() {
            if (details.open) {
                if (!bubbleIntervalId) {
                    bubbleIntervalId = setInterval(function() {
                        createBubble(particlesBg);
                    }, CONFIG.bubbleInterval);
                }
            } else {
                if (bubbleIntervalId) {
                    clearInterval(bubbleIntervalId);
                    bubbleIntervalId = null;
                }
            }
        });
    }

    setTimeout(init, 100);
    setTimeout(init, 500);
    setTimeout(init, 1000);
})();
