    <script>
        const CONFIG = {
            bubbleInterval: 400,
            bubbleSizeMin: 20, 
            bubbleSizeMax: 90,
            bubbleDurationMin: 10, 
            bubbleDurationMax: 18,
            // é€‚é…æµ…è‰²èƒŒæ™¯çš„æ°”æ³¡é¢œè‰²
            bubbleColors: [
                'rgba(244, 114, 182, 0.7)', // ä¸»ç²‰è‰²
                'rgba(249, 168, 212, 0.6)', // æ·¡ç²‰è‰²
                'rgba(251, 207, 232, 0.8)'  // æ¨±èŠ±ç²‰
            ]
        };

        const container = document.querySelector('.accordion-container');
        const header = document.querySelector('.accordion-header');
        const content = document.querySelector('.accordion-content');
        const particlesWrapper = document.querySelector('.particles-wrapper');
        let bubbleIntervalId = null;

        function toggleAccordion() {
            const isExpanded = container.classList.toggle('expanded');
            header.setAttribute('aria-expanded', isExpanded);
            content.setAttribute('aria-hidden', !isExpanded);
            if (isExpanded) { startBubbles(); } else { stopBubbles(); }
        }

        header.addEventListener('click', toggleAccordion);
        header.addEventListener('keydown', (e) => {
            if (e.key === 'Enter' || e.key === ' ') { 
                e.preventDefault(); 
                toggleAccordion(); 
            }
        });

        function createBubble() {
            if (!particlesWrapper) return;
            const bubble = document.createElement('div');
            bubble.className = 'bubble';
            const size = Math.random() * (CONFIG.bubbleSizeMax - CONFIG.bubbleSizeMin) + CONFIG.bubbleSizeMin;
            const duration = Math.random() * (CONFIG.bubbleDurationMax - CONFIG.bubbleDurationMin) + CONFIG.bubbleDurationMin;
            const color = CONFIG.bubbleColors[Math.floor(Math.random() * CONFIG.bubbleColors.length)];
            const swayX = (Math.random() - 0.5) * 120;
            const swayXEnd = (Math.random() - 0.5) * 120;
            bubble.style.width = `${size}px`; bubble.style.height = `${size}px`;
            bubble.style.left = `${Math.random() * 100}%`;
            bubble.style.background = `radial-gradient(circle at 35% 35%, ${color}, transparent 65%)`;
            bubble.style.animationDuration = `${duration}s`;
            bubble.style.animationDelay = `${Math.random() * 2}s`;
            bubble.style.setProperty('--sway-x', `${swayX}px`);
            bubble.style.setProperty('--sway-x-end', `${swayXEnd}px`);
            particlesWrapper.appendChild(bubble);
            setTimeout(() => bubble.remove(), (duration + 3) * 1000);
        }

        function startBubbles() {
            if (bubbleIntervalId) return;
            bubbleIntervalId = setInterval(createBubble, CONFIG.bubbleInterval);
        }

        function stopBubbles() {
            if (bubbleIntervalId) {
                clearInterval(bubbleIntervalId);
                bubbleIntervalId = null;
            }
        }

        document.addEventListener('visibilitychange', () => {
            if (document.hidden) { stopBubbles(); }
            else if (container.classList.contains('expanded')) { startBubbles(); }
        });
    </script>
